#include<iostream>using namespace std;const int MAXN = 9;   //申明常数MAXNbool used[MAXN];    //标记数字是否用过。int ans[MAXN];int n;void PaiLie(int k)   //当前讨论到第k的一位{    if (k == n + 1)        //此时表示前n个位置都填充好了，可以输出答案。    {        for (int i = 1; i <= n; i++) cout << ans[i];        cout << endl;        return;    }    for (int i = 1; i <= n; i++) //讨论第k个位置，可以填哪些数字    {        if (used[i] == 0)        //used[i]==0，表示数字i没有被用过，可以填到k位置        {            used[i] = true;      //将数字i标记为已使用            ans[k] = i;            //记录答案            PaiLie(k + 1);     //继续讨论下一位            ans[k] = 0;           //还原现场，将i标记为未使用            used[i] = 0;        }    }}int main(){    cin >> n;    PaiLie(1);//填充第1个位置到第n个位置    return 0;}